// src/components/DetailsSection.js
import React, { useState, useEffect } from 'react'; 
import { useApi } from '../context/ApiContext';
import BookForm from './BookForm';
import { useLocation } from 'react-router-dom';

const AddBook = () => {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);

    const { setSelectedBook, postData } = useApi();
    const [formData, setFormData] = useState({
        isbn13: '',
        isbn10: '',
        title: '',
        authors: '',
        categories: '',
        thumbnail: '',
        description: '',
        published_year: '',
        average_rating: '',
        num_pages: '',
        ratings_count: '',
    });

    // Fonction pour mettre à jour le livre sélectionné
    const handleBookSelect = (book) => {
        setSelectedBook(book);
    };

    useEffect(() => {
        // Récupérer les valeurs des paramètres de requête
        const bookParameter = searchParams.get('book');
    
        // Faire quelque chose avec les valeurs récupérées
        console.log('Paramètre 1:', bookParameter);
    }, [searchParams]);

    // Fonction pour traiter les données du formulaire
    const handleFormSubmit = async (formData) => {
        // Ajoutez ici la logique pour traiter les données du formulaire, par exemple, en les envoyant à votre backend
        console.log(formData);
        // Appel de la fonction postData pour envoyer les données du formulaire
        await postData('books', formData, 'POST');
        // Réinitialisez le formulaire après la soumission si nécessaire
        setFormData({
            isbn13: '',
            isbn10: '',
            title: '',
            authors: '',
            categories: '',
            thumbnail: '',
            description: '',
            published_year: '',
            average_rating: '',
            num_pages: '',
            ratings_count: '',
        });
    };
    
    return (
        <div className="container py-5">
            <h4 className='fw-bold'>Ajouter un livre</h4>
            {/* Ajoutez le formulaire ici */}
            <div className='row'>
                <div className='col-md-6 col-12 my-4'>
                    <BookForm initialFormData={formData} onSubmit={handleFormSubmit} />
                </div>
            </div>
        </div>
    );
};

export default AddBook;
