// BookList.js
import React, { useState, useEffect } from 'react'; 
import { useLocation } from 'react-router-dom';

const BookMore = () => {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const [ book, setBook ] = useState(null);

    useEffect(() => {
        setBook(searchParams.get('book'));
    }, []);
    

    return (
    <div className="modal fade" id={`infoModal${book.isbn13}`} tabindex="-1" role="dialog" 
        aria-labelledby={`exModalLabel${book.isbn13}`} aria-hidden="true">
        <div className="modal-dialog" role="document">
            <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title" id="exampleModalLabel">Informations importantes</h5>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="modal-body">
                    <p>
                        <span className="fw-bold">~</span>
                        <span className="text-warning fw-bold">{'{'}</span> 
                        <span className="fw-bold"> {book.title}</span>
                    </p>
                    <p className="ms-4"> 
                        <span>#</span> 
                        <span className="fw-bold text-primary">isbn13 : </span> 
                        {book.isbn13}
                    </p>
                    <p className="ms-4"> 
                        <span>#</span> 
                        <span className="fw-bold text-primary">isbn10 : </span> 
                        {book.isbn10}
                    </p>
                    <p className="ms-4"> 
                        <span>#</span> 
                        <span className="fw-bold text-primary text-small">by </span> 
                        {book.authors}
                    </p>
                    <p className="ms-4">
                        <span className="fw-bold text-primary badge bg-dark rounded text-light">{book.categories}</span>
                    </p>
                    <p> 
                        <span>#</span> 
                        <span className="font-weight-bold text-primary">Notes [</span>  {book.ratings_count}
                        <span className="font-weight-bold text-primary">-/-</span> {book.average_rating}
                        <span className="font-weight-bold text-primary">]</span>
                    </p>
                    <p> 
                        <span>#</span> 
                        <span className="font-weight-bold text-primary">description :</span> 
                        {book.description}
                    </p>
                </div>
                <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Fermer</button>
                </div>
            </div>
        </div>
    </div>
    );
};

export default BookMore;
