// src/components/DetailsSection.js
import React from 'react';
import { useApi } from '../context/ApiContext';
import BookForm from './BookForm';

const AddBook = () => {
    const [selectedBook, setSelectedBook] = useApi();

    // Fonction pour mettre à jour le livre sélectionné
    const handleBookSelect = (book) => {
        setSelectedBook(book);
    };

    // Fonction pour traiter les données du formulaire
    const handleFormSubmit = (formData) => {
        // Ajoutez ici la logique pour traiter les données du formulaire, par exemple, en les envoyant à votre backend
        console.log(formData);
        // Réinitialisez le formulaire après la soumission si nécessaire
        setFormData({
        isbn13: '',
        isbn10: '',
        title: '',
        authors: '',
        categories: '',
        thumbnail: '',
        description: '',
        published_year: '',
        average_rating: '',
        num_pages: '',
        ratings_count: '',
        });
    };


    
    return (
        <div className="container">
        <div className="row">
            <FeatureList />
            <DetailsSection onBookSelect={handleBookSelect} />
        </div>
        <br/><br/><br/>

        {selectedBook && <BookMore book={selectedBook} />}
        
        {/* Ajoutez le formulaire ici */}
        <BookForm onSubmit={handleFormSubmit} />
        </div>
    );
};

export default AddBook;
