// src/components/DetailsSection.js
import React from 'react';
import BookDetails from './BookDetails';
import { useApi } from '../context/ApiContext';

const DetailsSection = () => {
    // Exemple de données (à remplacer par vos données réelles)
    const bookDetailsData = {
        title: 'Gilead',
        isbn13: '9780002005883',
        isbn10: '9780002005883',
        author: 'Marilynne Robinson',
        genre: 'Fiction',
        year: '2004',
        pages: '297',
    };

    const { apiData } = useApi();
    
    return (
        <div className="col-lg-8 p-4">
            <p className="h1 fw-bold">Details.</p>
            <br/>
            <div className="row">
                <div className="col-md-12">
                    <p> <span className="text-success fw-bold">#</span> 
                        <span className="fw-bold">Agrégation pour les statistiques</span> 
                    </p>
                </div>
            </div>
            <div className="row col-md-12 rounded py-4">
                {apiData.map((book) => (
                    <BookDetails key={book.id} {...book} />
                ))}
                
                /*<BookDetails {...bookDetailsData} />*/
            </div>
        </div>
    );
};

export default DetailsSection;
