import React, { useEffect } from 'react';
import { useApi } from '../context/ApiContext';

const CategoryDropdown = () => {
    const { apiData, selectFeature } = useApi();

    useEffect(() => {
        // Chargez la liste des catégories au montage
        selectFeature('categories');
    }, [selectFeature]);

    const handleCategoryChange = (e) => {
        const selectedCategory = e.target.value;
        selectFeature('category/${selectedCategory}');
    };

    return (
        <div>
            <label htmlFor="categoryDropdown">Choisir une catégorie : </label>
            <select id="categoryDropdown" onChange={handleCategoryChange} value={apiData}>
                {apiData.map((category, index) => (
                    <option key={index} value={category}>
                        {category}
                    </option>
                ))}
            </select>
        </div>
    );
};

export default CategoryDropdown;
