// src/components/BookDetails.js
import React, { useRef } from 'react';
import BookMore from './BookMore';

const BookDetails = ({ title, isbn13, isbn10, authors, categories, published_year, num_pages }) => {

    const modalRef = useRef();

    const handleModalToggle = () => {
        // Utilisez la référence pour cibler le modal Bootstrap et déclencher l'ouverture
        if (modalRef.current) {
            const bootstrapModal = new window.bootstrap.Modal(modalRef.current);
            bootstrapModal.show();
        }
    };

    return (
        <div className="col-md-6 col-12 shadow p-4 rounded btn justify-content-start text-start" 
            data-toggle="modal" data-target={`#infoModal${isbn13}`} onClick={handleModalToggle}>
            <p>
                <span className="fw-bold">~ </span>
                <span className="text-warning fw-bold">{'{'}</span> 
                <span className="fw-bold"> {title}</span>
            </p>
            <p className="ms-4"> 
                <span>#</span> 
                <span className="fw-bold text-primary">isbn13 : </span> 
                {isbn13}
            </p>
            <p className="ms-4"> 
                <span>#</span> 
                <span className="fw-bold text-primary">isbn10 : </span> 
                {isbn10}
            </p>
            <p className="ms-4"> 
                <span>#</span> 
                <span className="fw-bold text-primary text-small">by </span> 
                {authors}
            </p>
            <p className="ms-4">
                <span className="fw-bold text-primary badge bg-dark rounded text-light">{categories}</span>
            </p>
            <p>
                <span className="text-warning fw-bold">{'}'}</span> 
                <span className="fw-bold text-primary">~ </span> 
                {published_year} | {num_pages}p
            </p>

            <div className="modal fade" ref={modalRef} tabIndex="-1" role="dialog">
                <BookMore />
            </div>
        </div>
    );
};

export default BookDetails;
