import React, { useEffect } from 'react';
import { useApi } from '../context/ApiContext';

const CategoryList = () => {
    const { apiDatas, getCategories, selectFeature } = useApi();

    useEffect(() => {
        // Log apiData for debugging
        // console.log(apiDatas);
        
        // Chargez la liste des catégories au montage
        getCategories('categories');
    }, [getCategories, apiDatas]);

    const handleCategoryChange = (e) => {
        const selectedCategory = e.target.value;
        selectFeature(`category/${selectedCategory}`);
    };

    // Check if apiData is an array before mapping
    const categoriesArray = Array.isArray(apiDatas) ? apiDatas : []; 

    return (
        <div className='rounded bg-warning text-white p-4'>
            {categoriesArray.map((item, index) => (
                <span key={index} className='badge'> {item} </span>
            ))}
        </div>
    );
};

export default CategoryList;
