// src/components/FeatureList.js
import React, { useEffect } from 'react';
import { useApi } from '../context/ApiContext';

const FeatureList = () => {

    const { fetchData } = useApi();

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleFeatureClick = (feature) => {
    fetchData(`books/${feature}`); // Utilisez la route API correspondant à la fonctionnalité
    };

    return (
        <div className="col-lg-4 p-4">
            <p className="fw-bold h5">Fonctionnalités.</p>
            <ul>
                <li className="feature-item" data-feature-id="books">Tous les livres</li>
                <li className="feature-item" data-feature-id="pages">Pages 2</li>
                <li className="feature-item" data-feature-id="rating">Rating</li>
            </ul>
            <ul>
            {apiData.map((item) => (
            <li key={item.id} className="feature-item" data-feature-id={item.id}>
                {item.name}
            </li>
            ))}
        </ul>
        </div>
    );
};

export default FeatureList;
