// src/components/FeatureList.js
import React, { useEffect } from 'react';
import { useApi } from '../context/ApiContext';

const FeatureList = () => {

    const { fetchData } = useApi();

    const handleFeatureClick = (feature) => {
        fetchData(`books/${feature}`); // Utilisez la route API correspondant à la fonctionnalité
    };

    return (
        <div className="col-lg-4 p-4">
            <p className="fw-bold h5">Fonctionnalités.</p>
            <ul>
                <li 
                    className="feature-item" 
                    data-feature-id="books"
                >
                    Tous les livres
                </li>
                <li 
                    className="feature-item" 
                    data-feature-id="category"
                    onClick={() => handleFeatureClick('category/romance')}
                >
                    Tous les livres par catégorie
                </li>
                <li
                    className="feature-item"
                    data-feature-id="year"
                    onClick={() => handleFeatureClick('year/2022')}
                >
                    Tous les livres de l'année
                </li>
            </ul>
        </div>
    );
};

export default FeatureList;
